import moment from "moment";
import axios from "/axios.js";
export default {
  name: "chart-current-sales-pie",
  created: function created() {
    this.FETCH_DATA();
  },
  data: function data() {
    return {
      chartData: {},
      chartOptions: {
        chart: {
          type: "pie"
        },
        credits: {
          enabled: false
        },
        title: {
          text: "THE REVENUE SUMMARY BY SERVICE TYPE IN 2022"
        },
        yAxis: {
          title: {
            text: "Value (Baht)"
          }
        },
        xAxis: {
          title: {// text: "Month",
          }
        },
        legend: {
          layout: "vertical",
          align: "right",
          verticalAlign: "middle"
        },
        plotOptions: {
          series: {
            label: {
              connectorAllowed: false
            },
            pointStart: 1
          },
          pie: {
            allowPointSelect: true,
            cursor: "pointer",
            dataLabels: {
              enabled: true,
              style: {
                fontSize: "14px"
              },
              formatter: function formatter() {
                return this.point.name + ": " + (this.point.y / 1000000).toFixed(2) + "MB";
              }
            }
          }
        },
        series: [{
          name: "Service",
          colorByPoint: true,
          data: [{
            name: "IDB",
            y: 0,
            color: "#3a0ca3"
          }, {
            name: "RBI",
            y: 0,
            color: "#7209b7"
          }, {
            name: "FFS",
            y: 0,
            color: "#4cc9f0"
          }, {
            name: "ITP",
            y: 0,
            color: "#4361ee"
          }]
        }],
        tooltip: {
          formatter: function formatter() {
            return "<b>" + this.point.name + "<br>" + "</b><br>Value: " + (this.y / 1000000).toFixed(2) + "MB (" + this.point.percentage.toFixed(2) + "%)";
          }
        },
        responsive: {
          rules: [{
            condition: {
              maxWidth: 500
            },
            chartOptions: {
              legend: {
                layout: "horizontal",
                align: "center",
                verticalAlign: "bottom"
              }
            }
          }]
        }
      }
    };
  },
  methods: {
    FETCH_DATA: function FETCH_DATA() {
      var _this = this;

      axios({
        method: "post",
        url: "current-sales/current-sales-group-sumbytype",
        headers: {
          Authorization: "Bearer " + JSON.parse(localStorage.getItem("token"))
        },
        data: {
          year_no: moment().year()
        }
      }).then(function (res) {
        // console.log(res);
        if (res.data) {
          _this.chartData = res.data;

          if (_this.chartData) {
            _this.chartOptions.series[0].data[0].y = _this.chartData[0].y;
            _this.chartOptions.series[0].data[1].y = _this.chartData[1].y;
            _this.chartOptions.series[0].data[2].y = _this.chartData[2].y;
            _this.chartOptions.series[0].data[3].y = _this.chartData[3].y;
          }
        }
      }).catch(function (error) {
        _this.$ons.notification.alert(error.code + " " + error.response.status + " " + error.message);
      }).finally(function () {});
    }
  },
  computed: {}
};
