export default {
  name: "FullScreenAlert",

  created() {},

  data() {
    return {
      alertText: {
        serverDown: {
          alertIcon: "las la-wifi",
          titleText: "Cannot connect to a server",
          desText1: 'Check your connection and click "Retry".',
          desText2: "If problem still persists, please contact support.",
          buttonIcon: "las la-undo-alt",
          buttonText: "Retry"
        },
        tokenExpired: {
          alertIcon: "las la-plug",
          titleText: "Session is expired",
          desText1: "Your access token is expired. Please sign in again.",
          desText2: "",
          buttonIcon: "las la-sign-in-alt",
          buttonText: "Go to Sign In Page"
        }
      }
    };
  },

  methods: {
    BTN_ACTION() {
      if (this.$store.state.status.server == false) this.$router.go();else if (this.$store.state.status.token == false) {
        localStorage.removeItem("user");
        localStorage.removeItem("token");
        this.$router.go();
      } else this.$router.go();
    }

  },
  computed: {
    alertIcon() {
      if (this.$store.state.status.server == false) return this.alertText.serverDown.alertIcon;else if (this.$store.state.status.token == false) return this.alertText.tokenExpired.alertIcon;else return "las la-exclamation-triangle";
    },

    titleText() {
      if (this.$store.state.status.server == false) return this.alertText.serverDown.titleText;else if (this.$store.state.status.token == false) return this.alertText.tokenExpired.titleText;else return "Unknown Error!";
    },

    desText1() {
      if (this.$store.state.status.server == false) return this.alertText.serverDown.desText1;else if (this.$store.state.status.token == false) return this.alertText.tokenExpired.desText1;else return "Please try refreshing the page.";
    },

    desText2() {
      if (this.$store.state.status.server == false) return this.alertText.serverDown.desText2;else if (this.$store.state.status.token == false) return this.alertText.tokenExpired.desText2;else return "If problem still persists, please contact support.";
    },

    buttonIcon() {
      if (this.$store.state.status.server == false) return this.alertText.serverDown.buttonIcon;else if (this.$store.state.status.token == false) return this.alertText.tokenExpired.buttonIcon;else return "las la-sync";
    },

    buttonText() {
      if (this.$store.state.status.server == false) return this.alertText.serverDown.buttonText;else if (this.$store.state.status.token == false) return this.alertText.tokenExpired.buttonText;else return "Refresh";
    }

  }
};
