import axios from "/axios.js"; // import clone from "just-clone";

import DxDateBox from "devextreme-vue/date-box";
import moment from "moment";
export default {
  name: "popup-add-weekly-report",
  components: {
    DxDateBox: DxDateBox
  },
  data: function data() {
    return {
      formData: {
        id_user: "",
        record_no: "",
        doc_seq: ""
      },
      formSelect: {
        now: new Date()
      }
    };
  },
  created: function created() {
    var id = JSON.parse(localStorage.getItem("user"));
    this.formData.id_user = id.id_user;
    this.GET_LAST_SEQ_NO();
  },
  methods: {
    GET_LAST_SEQ_NO: function GET_LAST_SEQ_NO() {
      var _this = this;

      var tbname = "WeeklyReport";
      axios({
        method: "post",
        url: "/global/last-doc-seq",
        headers: {
          Authorization: "Bearer " + JSON.parse(localStorage.getItem("token"))
        },
        data: {
          table_name: tbname
        }
      }).then(function (res) {
        console.log(res);

        if (res.status == 200 && res.data) {
          var curSeq = res.data[0].last_doc_seq;

          if (curSeq == null) {
            _this.formData.doc_seq = 1;
          } else {
            _this.formData.doc_seq = curSeq + 1;
          }

          if (_this.formData.doc_seq < 10) {
            _this.formData.doc_seq = "0" + _this.formData.doc_seq.toString();
          }

          _this.CAL_DOC_NO();
        }
      }).catch(function (error) {
        console.log(error);
      }).finally(function () {});
    },
    CAL_DOC_NO: function CAL_DOC_NO() {
      var month = moment().month() + 1;
      var year = moment().year();
      var monthyear;
      var s_month = month.toString();
      var s_year = year.toString();

      if (s_month.length == 1) {
        s_month = "0" + s_month;
      }

      s_year = s_year.slice(2, 4);
      monthyear = s_month.toString() + "-" + s_year.toString();
      this.formData.record_no = "AI-WSR-" + monthyear + "-" + this.formData.doc_seq.toString();
    },
    PREVIEW_IMG_UPLOAD: function PREVIEW_IMG_UPLOAD() {
      this.formData.file = this.$refs.file_img.files[0];
      var img = this.formData.file;

      if (img) {
        if (img.type == "image/png" || img.type == "image/jpeg") {
          if (img.size < 20000000) {
            document.getElementById("preview_img").src = window.URL.createObjectURL(img);
          } else {
            this.$ons.notification.alert("File size too large. (20 MB max)");
            this.PREVIEW_IMG_FORM_CLEAR();
          }
        } else {
          this.$ons.notification.alert("Incorrect filetype. <br/> Only PNG/JPG/JPEG file can be uploaded.");
          this.PREVIEW_IMG_FORM_CLEAR();
        }
      }
    },
    PREVIEW_IMG_DELETE: function PREVIEW_IMG_DELETE() {
      this.formData.file = "";
      document.getElementById("preview_img").src = "";
    },
    PREVIEW_IMG_FORM_CLEAR: function PREVIEW_IMG_FORM_CLEAR() {
      var file = document.getElementById("preview_input_img");
      file.val = "";
      this.formData.file = "";
    },
    SAVE: function SAVE() {
      var _this2 = this;

      if (this.formData.start_date) {
        if (this.formData.end_date) {
          this.$ons.notification.confirm("Confirm save?").then(function (res) {
            if (res == 1) {
              var data = _this2.formData;
              axios({
                method: "post",
                url: "/weekly-report/weekly-report-add",
                headers: {
                  Authorization: "Bearer " + JSON.parse(localStorage.getItem("token"))
                },
                data: data
              }).then(function (res) {
                if (res.status == 200) {
                  _this2.$ons.notification.alert("Add successful");

                  _this2.$emit("refreshInfo");

                  _this2.$emit("btn-cancel-add");
                }
              }).catch(function (error) {
                _this2.$ons.notification.alert(error.code + " " + error.response.status + " " + error.message);
              }).finally(function () {});
            }
          });
        } else {
          this.$ons.notification.alert('"End Date" field cannot be empty');
        }
      } else {
        this.$ons.notification.alert('"Start Date" field cannot be empty');
      }
    },
    CANCEL: function CANCEL() {
      var _this3 = this;

      // console.log(this.formData);
      var form = this.formData; // console.log(form);

      if (this.formData != form) {
        this.$ons.notification.confirm("Your unsaved changes will be lost").then(function (res) {
          if (res == 1) {
            _this3.$emit("btn-cancel-add");
          }
        });
      } else {
        this.$emit("btn-cancel-add");
      }
    }
  }
};
