import ChartProjectProgress from "@/views/Applications/ExecutiveManagement/Charts/project-progress-line.vue"; //Structures

import contentLoading from "@/components/app-structures/app-content-loading.vue";
import toolbar from "@/components/app-structures/app-toolbar.vue"; //API

import axios from "/axios.js";
import moment from "moment";
export default {
  name: "ViewProjectProgress",
  components: {
    toolbar: toolbar,
    contentLoading: contentLoading,
    ChartProjectProgress: ChartProjectProgress
  },
  created: function created() {
    this.$store.commit("UPDATE_CURRENT_INAPP", {
      name: "Project Progress",
      icon: "/img/icon_menu/executive_management/progress.png"
    });
    if (this.$store.state.status.server == true) this.FETCH_DATA();
  },
  computed: {
    report_date: function report_date() {
      return moment(this.currentViewRow.report_date).format("dddd, LL");
    }
  },
  data: function data() {
    return {
      isAdd: false,
      isEdit: false,
      isLoading: false,
      current_project_progress_chart: []
    };
  },
  mounted: function mounted() {},
  methods: {
    FETCH_DATA: function FETCH_DATA() {
      var _this = this;

      this.isLoading = true;
      axios({
        method: "get",
        url: "/current-sales/project-current-sales-progress",
        headers: {
          Authorization: "Bearer " + JSON.parse(localStorage.getItem("token"))
        }
      }).then(function (res) {
        // console.log(res);
        if (res.data) {
          _this.current_project_progress_chart = res.data;
        }
      }).catch(function (error) {
        _this.$ons.notification.alert(error.code + " " + error.response.status + " " + error.message);
      }).finally(function () {
        _this.isLoading = false;
      });
    }
  }
};
