export default function(ons) {
  return Object.keys(ons)
    .filter(k => [
      /^is/,
      /^disable/,
      /^enable/,
      /^mock/,
      /^open/,
      /^set/,
      /animit/,
      /elements/,
      /GestureDetector/,
      /notification/,
      /orientation/,
      /platform/,
      /ready/,
    ].some(t => k.match(t)))
    .reduce((r, k) => {
      r[k] = ons[k];
      return r;
    }, { _ons: ons });
}
export const hyphenate = string => string.replace(/([a-zA-Z])([A-Z])/g, '$1-$2').toLowerCase();

export const capitalize = string => string.charAt(0).toUpperCase() + string.slice(1);

export const camelize = string => string.toLowerCase().replace(/-([a-z])/g, (m, l) => l.toUpperCase());

export const eventToHandler = name => '_on' + capitalize(name);

export const handlerToProp = name => name.slice(2).charAt(0).toLowerCase() + name.slice(2).slice(1);
import { camelize, eventToHandler, handlerToProp } from '../internal/util';

/* Private */
const _setupDBB = component => {
  const dbb = 'onDeviceBackButton';
  // Call original handler or parent handler by default
  const handler = component[dbb] || (component.$el[dbb] && component.$el[dbb]._callback) || (e => e.callParentHandler());

  component.$el[dbb] = event => {
    let runDefault = true;

    component.$emit(handlerToProp(dbb), {
      ...event,
      preventDefault: () => runDefault = false
    });

    runDefault && handler(event);
  };

  component._isDBBSetup = true;
};

/* Public */
// Device Back Button Handler
const deriveDBB = {
  mounted() {
    _setupDBB(this);
  },

  // Core destroys deviceBackButton handlers on disconnectedCallback.
  // This fixes the behavior for <keep-alive> component.
  activated() {
    this._isDBBSetup === false && _setupDBB(this);
  },

  deactivated() {
    this._isDBBSetup === true && (this._isDBBSetup = false);
  },

  destroyed() {
    this.$el.onDeviceBackButton && this.$el.onDeviceBackButton.destroy();
  }
};

const deriveEvents = {
  computed: {
    unrecognizedListeners() {
      const name = camelize('-' + this.$options._componentTag.slice(6));
      return Object.keys(this.$listeners || {})
        .filter(k => (this.$ons.elements[name].events || []).indexOf(k) === -1)
        .reduce((r, k) => {
          r[k] = this.$listeners[k];
          return r;
        }, {});
    }
  },

  mounted() {
    this._handlers = {};

    (this.$el.constructor.events || []).forEach(key => {
      this._handlers[eventToHandler(key)] = event => {
        // Filter events from different components with the same name
        if (event.target === this.$el || !/^ons-/i.test(event.target.tagName)) {
          this.$emit(key, event);
        }
      };
      this.$el.addEventListener(key, this._handlers[eventToHandler(key)]);
    });
  },

  beforeDestroy() {
    Object.keys(this._handlers).forEach(key => {
      this.$el.removeEventListener(key, this._handlers[key]);
    });
    this._handlers = null;
  }
};

export { deriveDBB, deriveEvents };
/* Private */
const _toggleVisibility = function() {
  if (typeof this.visible === 'boolean' && this.visible !== this.$el.visible) {
    this.$el[this.visible ? 'show' : 'hide'].call(this.$el, this.normalizedOptions || this.options);
  }
};
const _teleport = function() {
  if (!this._isDestroyed && (!this.$el.parentNode || this.$el.parentNode !== document.body)) {
    document.body.appendChild(this.$el);
  }
};
const _unmount = function() {
  if (this.$el.visible === true) {
    this.$el.hide().then(() => this.$el.remove());
  } else {
    this.$el.remove();
  }
};

/* Public */
// Components that can be shown or hidden
const hidable = {
  props: {
    visible: {
      type: Boolean,
      default: undefined // Avoid casting to false
    }
  },

  watch: {
    visible() {
      _toggleVisibility.call(this);
    }
  },

  mounted() {
    this.$nextTick(() => _toggleVisibility.call(this));
  },

  activated() {
    this.$nextTick(() => _toggleVisibility.call(this));
  }
};

// Components with 'options' property
const hasOptions = {
  props: {
    options: {
      type: Object,
      default() {
        return {};
      }
    }
  }
};

// Provides itself to its descendants
const selfProvider = {
  provide() {
    return {
      [this.$options._componentTag.slice(6)]: this
    }
  }
};

// Common event for Dialogs
const dialogCancel = {
  mounted() {
    this.$on('dialog-cancel', () => this.$emit('update:visible', false));
  }
};

// Moves the element to a global position
const portal = {
  mounted() {
    _teleport.call(this);
  },
  updated() {
    _teleport.call(this);
  },
  activated() {
    _teleport.call(this);
  },
  deactivated() {
    _unmount.call(this);
  },
  beforeDestroy() {
    _unmount.call(this);
  }
};

const modifier = {
  props: {
    modifier: {
      type: [String, Array, Object]
    },
  },

  computed: {
    normalizedModifier() {
      const modifier = this.modifier;

      if (typeof modifier === 'string') {
       return modifier;
      }

      if (Array.isArray(modifier)) {
        return modifier.join(' ');
      }

      if (typeof modifier === 'object') {
        return Object.keys(modifier)
          .reduce((acc, key) => (acc + (modifier[key] ? ` ${key}` : '')), '')
          .trim();
      }

      return false;
    }
  }
};

export { hidable, hasOptions, selfProvider, dialogCancel, portal, modifier };
/* Private */
const model = {
  prop: 'modelProp',
  event: 'modelEvent'
};

/* Public */

// Generic input
const modelInput = {
  model,
  props: {
    [model.prop]: [Number, String],
    [model.event]: {
      type: String,
      default: 'input'
    }
  },

  methods: {
    _updateValue() {
      if (this[model.prop] !== undefined && this.$el.value !== this[model.prop]) {
        this.$el.value = this[model.prop];
      }
    },
    _onModelEvent(event) {
      this.$emit(model.event, event.target.value);
    }
  },

  watch: {
    [model.prop]() {
      this._updateValue();
    }
  },

  mounted() {
    this._updateValue();
    this.$el.addEventListener(this[model.event], this._onModelEvent);
  },
  beforeDestroy() {
    this.$el.removeEventListener(this[model.event], this._onModelEvent);
  }
};

// Checkable inputs
const modelCheckbox = {
  mixins: [modelInput],

  props: {
    [model.prop]: [Array, Boolean],
    [model.event]: {
      type: String,
      default: 'change'
    }
  },

  methods: {
    _updateValue() {
      if (this[model.prop] instanceof Array) {
        this.$el.checked = this[model.prop].indexOf(this.$el.value) >= 0;
      } else {
        this.$el.checked = this[model.prop];
      }
    },
    _onModelEvent(event) {
      const { value, checked } = event.target;
      let newValue;

      if (this[model.prop] instanceof Array) {
        // Is Array
        const index = this[model.prop].indexOf(value);
        const included = index >= 0;

        if (included && !checked) {
          newValue = [
            ...this[model.prop].slice(0, index),
            ...this[model.prop].slice(index + 1, this[model.prop].length)
          ];
        }

        if (!included && checked) {
          newValue = [ ...this[model.prop], value ];
        }

      } else {
        // Is Boolean
        newValue = checked;
      }

      // Emit if value changed
      newValue !== undefined && this.$emit(model.event, newValue);
    }
  }
};

// Radio input
const modelRadio = {
  mixins: [modelInput],
  props: {
    [model.event]: {
      type: String,
      default: 'change'
    }
  },

  methods: {
    _updateValue() {
      this.$el.checked = this[model.prop] === this.$el.value;
    },
    _onModelEvent(event) {
      const { value, checked } = event.target;
      checked && this.$emit(model.event, value);
    }
  }
};

export { modelInput, modelCheckbox, modelRadio };

