      // of Android inconsistency.
      let nIter = 0;
      const interval = setInterval(() => {
        nIter++;

        const w = window.innerWidth;
        const h = window.innerHeight;

        if ((isPortrait && w <= h) ||
           (!isPortrait && w >= h)) {
          this.emit('change', {isPortrait: isPortrait});
          clearInterval(interval);
        } else if (nIter === 50) {
          this.emit('change', {isPortrait: isPortrait});
          clearInterval(interval);
        }
