export class ActionSheetAnimator extends BaseAnimator {

  constructor({timing = 'linear', delay = 0, duration = 0.2} = {}) {
    super({ timing, delay, duration })
  }

  /**
   * @param {HTMLElement} dialog
   * @param {Function} done
   */
