  }

  /**
   * @param {Object} dialog
   * @param {Function} callback
   */
  show(dialog, callback) {
    animit.runAll(

      animit(dialog._mask)
      .queue({ opacity: 0 })
      .wait(this.delay)
      .queue({ opacity: 1 }, {
        duration: this.maskDuration,
        timing: this.maskTiming
      }),

      animit(dialog._sheet, this.def)
        .default(
          { transform: `translate3d(0, ${this.liftAmount}, 0)` },
          { transform: 'translate3d(0, 0, 0)' }
        )
        .queue(done => {
          callback && callback();
          done();
        })
    );
  }

  /**
   * @param {Object} dialog
   * @param {Function} callback
   */
  hide(dialog, callback) {
    animit.runAll(

      animit(dialog._mask)
      .queue({ opacity: 1 })
      .wait(this.delay)
      .queue({ opacity: 0 }, {
        duration: this.maskDuration,
        timing: this.maskTiming
      }),

      animit(dialog._sheet, this.def)
        .default(
          { transform: 'translate3d(0, 0, 0)' },
          { transform: `translate3d(0, ${this.liftAmount}, 0)` }
        )
        .queue(done => {
          callback && callback();
          done();
        })
    );
  }
}
