    switch (name) {
      case 'swipeable':
        this._swiper.updateSwipeable(this.hasAttribute('swipeable'));
        break;
      case 'auto-refresh':
        this._swiper.updateAutoRefresh(this.hasAttribute('auto-refresh'));
        break;
      case 'item-height':
        this.vertical && this._swiper.updateItemSize(this.itemSize);
        break;
      case 'item-width':
        this.vertical || this._swiper.updateItemSize(this.itemSize);
        break;
      case 'direction':
        this._swiper.refresh();
    }
