      e.ripple = true;
      this._updateParent();
      this._rippleAnimation(e.gesture.srcEvent).then(() => {
        this._animator.fade(this._wave);
        this._animator.fade(this._background);
      });
    }
  }

  _onHold(e) {
    if (!this.disabled && !e.ripple) {
      e.ripple = true;
      this._updateParent();
      this._holding = this._rippleAnimation(e.gesture.srcEvent, 2000);
