import axios from "/axios.js";
import clone from "just-clone";
import DxDateBox from "devextreme-vue/date-box"; // import moment from "moment";

export default {
  name: "popup-edit-gasbill",
  components: {
    DxDateBox: DxDateBox
  },
  props: {
    editInfo: Object
  },
  data: function data() {
    return {
      formData: {},
      formSelect: {
        now: new Date()
      }
    };
  },
  computed: {
    baseURL: function baseURL() {
      var mode = this.$store.state.mode;
      if (mode == "dev") return this.$store.state.modeURL.dev;else if (mode == "prod") return this.$store.state.modeURL.prod;else return console.log("develpment mode set up incorrect.");
    }
  },
  created: function created() {
    this.formData = clone(this.editInfo);
    var id = JSON.parse(localStorage.getItem("user"));
    this.formData.id_user = id.id_user;
    this.formData.doc_seq = parseInt(this.formData.doc_seq);
  },
  mounted: function mounted() {
    if (this.formData.receipt_img != null) {
      document.getElementById("preview_img").src = this.baseURL + this.formData.receipt_img;
    }
  },
  methods: {
    PREVIEW_IMG_UPLOAD: function PREVIEW_IMG_UPLOAD() {
      this.formData.file = this.$refs.file_img.files[0];
      var img = this.formData.file;

      if (img) {
        if (img.type == "image/png" || img.type == "image/jpeg") {
          if (img.size < 20000000) {
            document.getElementById("preview_img").src = window.URL.createObjectURL(img);
          } else {
            this.$ons.notification.alert("File size too large. (20 MB max)");
            this.PREVIEW_IMG_FORM_CLEAR();
          }
        } else {
          this.$ons.notification.alert("Incorrect filetype. <br/> Only PNG/JPG/JPEG file can be uploaded.");
          this.PREVIEW_IMG_FORM_CLEAR();
        }
      }
    },
    PREVIEW_IMG_DELETE: function PREVIEW_IMG_DELETE() {
      this.formData.file = null;
      this.formData.receipt_img = null;
      document.getElementById("preview_img").src = "";
    },
    PREVIEW_IMG_FORM_CLEAR: function PREVIEW_IMG_FORM_CLEAR() {
      var file = document.getElementById("preview_input_img");
      file.val = "";
      this.formData.file = null;
    },
    SAVE: function SAVE() {
      var _this = this;

      if (this.formData.bill_date) {
        if (this.formData.price) {
          this.$ons.notification.confirm("Confirm save?").then(function (res) {
            if (res == 1) {
              var data = _this.formData;
              axios({
                method: "put",
                url: "/fuel-bill/fuel-bill-edit",
                headers: {
                  "Content-Type": "multipart/form-data",
                  Authorization: "Bearer " + JSON.parse(localStorage.getItem("token"))
                },
                data: data
              }).then(function (res) {
                // console.log(res.data);
                if (res.status == 200) {
                  _this.$ons.notification.alert("Bill Record Edit successful");

                  _this.$emit("btn-cancel-edit");

                  _this.$emit("refreshList");
                }
              }).catch(function (error) {
                _this.$ons.notification.alert(error.code + " " + error.response.status + " " + error.message);
              }).finally(function () {});
            }
          });
        } else {
          this.$ons.notification.alert('"Price" field cannot be empty');
        }
      } else {
        this.$ons.notification.alert('"Bill Date" field cannot be empty');
      }
    },
    CANCEL: function CANCEL() {
      var _this2 = this;

      // console.log(this.formData);
      var form = this.formData; // console.log(form);

      if (this.formData != form) {
        this.$ons.notification.confirm("Your unsaved changes will be lost").then(function (res) {
          if (res == 1) {
            _this2.$emit("btn-cancel-edit");
          }
        });
      } else {
        this.$emit("btn-cancel-edit");
      }
    }
  }
};
